    public static int findMaxConsecutiveOnes(int[] nums) {
        int max = 0;
        int current = 0;

        for (int i = 0; i < nums.length; i++) {

            if (nums[i] == 1) {
                current++;
            }

            if (i == nums.length - 1 || nums[i] == 0) {
                if (max == 0 || current > max) {
                    max = current;
                }
                current = 0;
            }
        }
        return max;
    }
